import React, { useRef, useState } from 'react';   /* импортируем useRef */
import PostList from './components/postList';
import MyButton from './components/UI/button/MyButton';
import MyInput from './components/UI/input/MyInput';
import './styles/App.css';


function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'Javascript', body: 'Description' },
    { id: 2, title: 'Javascript 2', body: 'Description' },
    { id: 3, title: 'Javascript 3', body: 'Description' },

  ])


  const [title, setTitle] = useState('')    /* создаем состояние с пустой строкой */
  const [body, setBody] = useState('')    /* создаем состояние с пустой строкой */


  const addNewPost = (e) => {     /* создаем эту функцию */
  e.preventDefault()          /* вызываем функцию preventDefault, которая предотвращает дефолтное поведение браузера */
    const newPost = {    /* создаем новый обьект и передаем туда массив*/
      id:Date.now(),
      title,    /* передаем заголовок */
      body      /* передаем боди */
    }
    console.log(newPost)

  }

  return (
    <div className="App">
      <form>
        {/* УПРАВЛЯЕМЫЙ КОМПОНЕНТ */}
        <MyInput

          value={title}    /* Связываем валью инпута с состонием title */
          onChange={e => setTitle(e.target.value)}    /* добавляем функцию onChange что бы видеть как пользователь что-то вводит */
          type="text"
          placeholder='Название поста' />

        {/* НЕУПРАВЛЯЕМЫЙ КОМПОНЕНТ */}
        <MyInput
          value={body}    /* Связываем валью инпута с состонием title */
          onChange={e => setBody(e.target.value)}    /* добавляем функцию onChange что бы видеть как пользователь что-то вводит */
          type="text"
          placeholder='Описание поста' />
        <MyButton onClick={addNewPost}>Создать пост</MyButton>      {/* вешаем слушатель события на кнопку и при нажатии добавляем фугкцию */}
      </form>
      <PostList posts={posts} title="посты про js 1" />
    </div>
  );
}

export default App;
