import React, { useState } from 'react';
import PostList from './components/postList';
import './styles/App.css';


function App() {
  const [posts, setPosts] = useState([   /* создаем хук useState */
    { id: 1, title: 'Javascript', body: 'Description' },  /*передаем массив */
    { id: 2, title: 'Javascript 2', body: 'Description' },  /*передаем массив */
    { id: 3, title: 'Javascript 3', body: 'Description' },  /*передаем массив */

  ])

  return (
    <div className="App">

      <input type="text" placeholder='Название поста'/>   создаем новый пост 
      <input type="text" placeholder='Описание поста'/>
      <button>Создать пост</button>

      <PostList posts={posts} title = "посты про js 1"/>    {/* передаем PostList и массив что в пропсах posts и title */}

    </div>
  );
}

export default App;
