import React from "react";
import PostItem from ""

const PostList = ({ posts }) => {             /* принимаем пропсы через обьект */
    return (
        <div>
            <h1 style={{ textAlign: 'center' }}> {/* выравниваем зашоловок по центру */}
                Список постов
            </h1>
            {posts.map(post =>              /*создаем функцию map - эта функция позволят преобразовать массив обычных элементов в реакт элементы*/
                <PostItem post={post} key={post.id} />     /*отрисовываем PostItem и как пропс передаем туда обьект post */
            )}                                    {/* /* указываем ключь key, при этом от должен хранить уникальное значение это id обьекта. (ключи позволяют алгоритмам реакта перересовывать не весь список, а только те в которых произошли изменения) */}

        </div>
    );
};

export default PostList;
