import React from "react";


const MySelect = ({options,defaultValue,value,onChange}) => {
    return (
        <select value={value}>
                
            <option disabled value="">{defaultValue}</option>   {/* добовляем дефолтную опцию сортировка по */}
            {options.map(option =>       /* итерируемся с помощью map */
                <option key={options.value} value={option.value}>  {/* для каждой отции отрисовываем html тэг (value - поле из обьекта) и добавляем уникаьный ключ*/}
                    {option.name}     {/* сам текст дотаем из поля name */}
                </option>
                )}
        </select>
    )
}


export default MySelect;
