import PostItem from "./postItem"
import {TransitionGroup} from "react-transition-group";

const PostList = ({ posts, title, remove }) => {     
    
    if (!posts.length) {
        return(
        <h1 style={{ textAlign: 'center' }}>
          Посты не найдены
        </h1>
        )
    }
    


    return (
        <div>
            <h1 style={{ textAlign: 'center' }}> 
                {title}    
            </h1>
            <TransitionGroup>   
            {posts.map((post, index) =>       
                <CSSTransition
