import React, {  useMemo, useState } from 'react';  
import PostForm from './components/postForm';
import PostList from './components/postList';
import './styles/App.css';

import MySelect from '../src/components/UI/select/MySelect'   
import MyInput from './components/UI/input/MyInput';

function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'ffdf', body: 'fdw' },
    { id: 2, title: 'awewe 2', body: 'Description' },
    { id: 3, title: 'cxvb 3', body: 'yjy' },

  ])


 const [filter,setFilter] = useState({sort:'',query:''})


  const sortedPosts = useMemo( () => {  
    if(filter.sort) {
      return [...posts].sort((a,b)=>a[filter.sort].localeCompare(b[filter.sort])) 
    }
      return posts;
    }, [filter.sort, posts])

  const sortedAndSearcedPosts = useMemo(() =>{  
    return sortedPosts.filter(post => post.title.toLowerCase().includes(filter.query)) 
  },[filter.query,sortedPosts])

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }



  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))

  }


  return (
    <div className="App">
      <PostForm create={createPost} />
      <hr style={{ margin: '15px 0' }}/> 
      <PostFilter 
        filter={filter}
        setFilter={setFilter}
        />
      {sortedAndSearcedPosts.length !== 0   
        ? <PostList remove={removePost} posts={sortedAndSearcedPosts} title="посты про Javascript" />   
        : <h1 style={{ textAlign: 'center' }}>
          Посты не найдены
        </h1>
      }

    </div>
  );
}

export default App;
