import React, { useState } from 'react';

function App() {

  const [likes, setLikes] = useState(5)  /* создаем состояние - likes, setLikes - функция которое состояние изменяет, создаем функцию useState и импортируем ее */ 
  const [value,setValue] = useState("текст в импуте")                             

  function increment() {     /* создаем функцию с increment */
     setLikes(likes +1)  
  }

  function decrement() { 
    setLikes(likes -1)    /* создаем функцию с переменной decrement */

  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <h1>{value}</h1>
      <input type="text" value={value}        /*  добпаляем атрибут value и состояние {value} */ 
      onChange={event =>setValue(event.target.value)}/>   слушатель события onChange, еы
      <button onClick={increment}>Increment</button>   { /* вешаем слушатель события onClick */ }
      <button onClick={decrement}>Decrement</button>

    </div>
  );
}

export default App;
