import React, {  useMemo, useState } from 'react';  
import PostForm from './components/postForm';
import PostList from './components/postList';
import './styles/App.css';
import PostFilter from './components/postFilter';
import MyModal from './components/UI/myModal/MyModal';
import MyButton from './components/UI/button/MyButton';


function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'ffdf', body: 'fdw' },
    { id: 2, title: 'awewe 2', body: 'Description' },
    { id: 3, title: 'cxvb 3', body: 'yjy' },

  ])


 const [filter,setFilter] = useState({sort:'',query:''})
 const [modal,setModal] =useState(false);  /* 6 создаем состояние видимости модального окна */


  const sortedPosts = useMemo( () => {  
    if(filter.sort) {
      return [...posts].sort((a,b)=>a[filter.sort].localeCompare(b[filter.sort])) 
    }
      return posts;
    }, [filter.sort, posts])

  const sortedAndSearcedPosts = useMemo(() =>{  
    return sortedPosts.filter(post => post.title.toLowerCase().includes(filter.query)) 
  },[filter.query,sortedPosts])

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
    setModal(false)
  }



  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))

  }


  return (
    <div className="App">
      <MyButton style={{marginTop: 30}} onClick={()=>setModal(true)}> {/* 9 при нажатии изменяем модал true */}
        Создать пользователя     {/* 8 создаем кнопку при нажатии на которую будет всплывать модальное окно */}
      </MyButton>
      <MyModal visible={modal} setVisible={setModal}>  {/* 7 передаем это состояние в качестве пропров, а функию которое это состояние изменяет передаем как пропс setVisible */} 
        <PostForm create={createPost} />
      </MyModal>

      <hr style={{ margin: '15px 0' }}/> 
      <PostFilter 
        filter={filter}
        setFilter={setFilter}
        />
         <PostList remove={removePost} posts={sortedAndSearcedPosts} title="посты про Javascript" />   
    </div>
  );
}

export default App;
