import React, {  useMemo, useState } from 'react';  /* импортируем хук useMemo */
import PostForm from './components/postForm';
import PostList from './components/postList';
import './styles/App.css';
import MySelect from '../src/components/UI/select/MySelect'   
import MyInput from './components/UI/input/MyInput';

function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'ffdf', body: 'fdw' },
    { id: 2, title: 'awewe 2', body: 'Description' },
    { id: 3, title: 'cxvb 3', body: 'yjy' },

  ])


  const [selectedSort,setSelectedSort] = useState('')   
  const [searсhQuery,setSearchQuery] = useState('')    /* создаем состояние searhcQuery с функцией изсенения setSearchQuery */


  const sortedPosts = useMemo( () => {
    console.log('ОТРАБОТАЛА ФУНКЦИЯ sortedPosts')
    if(selectedSort) {
      return [...posts].sort((a,b)=>a[selectedSort].localeCompare(b[selectedSort]))
    }
      return posts;
    }, [selectedSort, posts])

  const sortedAndSearcedPosts = useMemo()

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }



  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))

  }

  const sortPosts = (sort) => {    
    setSelectedSort(sort);   
  }


  return (
    <div className="App">
      <PostForm create={createPost} />
      <hr style={{ margin: '15px 0' }}/> 
      <div>
        <MyInput
          value={searсhQuery}     /* передаем searсhQuery */
          onChange={e=>setSearchQuery(e.target.value)}  /* вешеаем слушатель событий onChange,  */
          placeholder="Поиск..."   /* добавляем строку поиска */
        />

        <MySelect    
          value={selectedSort}   
          onChange={sortPosts}  
          defaultValue="Сортировка"   
          options={[      
            {value: 'title', name:'По названию'},    
            {value: 'body', name:'По описанию'},       
          ]} 
          />

      </div>
      {posts.length !== 0
        ? <PostList remove={removePost} posts={sortedPosts} title="посты про Javascript" />
        : <h1 style={{ textAlign: 'center' }}>
          Посты не найдены
        </h1>
      }

    </div>
  );
}

export default App;
