export const getPageCount = (totalCount, limit) => {   /* 8 создаем фугкцию для работф с номерами страниц. Агрументом будет общще количесво */
    return Math.ceil(totalCount/limit)
}


export const getPagesArray = (totalPages) => {
    let result = [];
    for (let i = 0; i < totalPages; i++) {
        result.push(i + 1)
    }
    return result;
}
