import React, { useState } from 'react';

function App() {

  const [likes, setLikes] = useState(5)  
  /* создаем состояние */
  /* создаем функцию useState и импортируем ее */ 


  function increment() {     /* создаем функцию с increment */
     setLikes(likes +1)  
  }

  function decrement() { 
    setLikes(likes -1)    /* создаем функцию с переменной decrement */

  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <button onClick={increment}>Increment</button> { /* вешаем слушатель события onClick */ }
      <button onClick={decrement}>Decrement</button>

    </div>
  );
}

export default App;
