import React, { useEffect, useState } from 'react';
import PostForm from './components/postForm';
import PostList from './components/postList';
import './styles/App.css';
import PostFilter from './components/postFilter';
import MyModal from './components/UI/myModal/MyModal';
import MyButton from './components/UI/button/MyButton';
import { usePosts } from './hooks/usePost';
import axios from 'axios' /*  2 импортруем */ 


function App() {
  const [posts, setPosts] = useState([])
  const [filter, setFilter] = useState({ sort: '', query: '' })
  const [modal, setModal] = useState(false);
  const sortedAndSearcedPosts = usePosts(posts, filter.sort, filter.query)   


  useEffect(() => {
    fetchPosts()
  }, [])


  const createPost = (newPost) => {
    setPosts([...posts, newPost])
    setModal(false)
  }

  async function fetchPosts() {    /* 1 создаем функцию которая отправляет запрос на сервер, получать данные и помещать в посты */
    const response = await axios.get('https://jsonplaceholder.typicode.com/posts')  /* 2 создаем переменную куда помещен результат выполнения запроса и вставляем адрес*/
    setPosts(response.data)
  }


  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))

  }


  return (
    <div className="App">
      <button onClick={fetchPosts}>GET POSTS</button>  {/*  4 добавляем кнопку при которой будут появляться загруженные посты */}
      <MyButton style={{ marginTop: 30 }} onClick={() => setModal(true)}>
        Создать пользователя
      </MyButton>
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost} />
      </MyModal>

      <hr style={{ margin: '15px 0' }} />
      <PostFilter
        filter={filter}
        setFilter={setFilter}
      />
      <PostList remove={removePost} posts={sortedAndSearcedPosts} title="посты про Javascript" />
    </div>
  );
}

export default App;
