import React from "react";
import PostItem from "./postItem"

const PostList = ({ posts,title }) => {            
    return (
        <div>
            <h1 style={{ textAlign: 'center' }}> 
                {title}    
            </h1>
            {posts.map((post, index) =>             
                <PostItem  number={index + 1} post={post} key={post.id} />     
            )}                                    {/* /* указываем ключь key, при этом от должен хранить уникальное значение это id обьекта. (ключи позволяют алгоритмам реакта перересовывать не весь список, а только те в которых произошли изменения) */}

        </div>
    );
};

export default PostList;
