import React, { useRef, useState } from 'react';
import PostForm from './components/postForm';
import PostList from './components/postList';
import './styles/App.css';
import MySelect from '../src/components/UI/select/MySelect'   /* импортируем  */

function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'Javascript', body: 'Description' },
    { id: 2, title: 'Javascript 2', body: 'Description' },
    { id: 3, title: 'Javascript 3', body: 'Description' },

  ])


  const [selectedSort,setSelectedSort] = useState('')    /* создаем двухстороннее связывение selectedSort, с помощью useState инициализируем */

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }



  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))

  }

  const sortPosts = (sort) => {    /* создаем функцию для сортирвки массива */

    console.log(sort)
  }


  return (
    <div className="App">
      <PostForm create={createPost} />
      <hr style={{ margin: '15px 0' }}></hr>  
      <div>
        <MySelect     /* заменяем селект  */
          value={selectedSort}   /* в качестве value передаем selectedSort */
          onChange={sortPosts}   /*  вызываем функцию и передаем туда setSelectedSort, та опция которую выбрал пользователь */
          defautValue="Сортировка"   /* передаем необходимые пропсы */
          options={[       /* передаем массив опций с двумя обьектами */
            {value: 'title', name:'По названию'},     /* обьекты с сортировкаой по заголовку  */
            {value: 'body', name:'По описанию'}        /*обьекты с сортировкаой по телу */
          ]} 
          />

      </div>
      {posts.length !== 0
        ? <PostList remove={removePost} posts={posts} title="посты про Javascript" />
        : <h1 style={{ textAlign: 'center' }}>
          Посты не найдены
        </h1>
      }

    </div>
  );
}

export default App;
