import React, {  useMemo, useState } from 'react';  /* 8 импортируем хук useMemo */
import PostForm from './components/postForm';
import PostList from './components/postList';
import './styles/App.css';
import MySelect from '../src/components/UI/select/MySelect'   
import MyInput from './components/UI/input/MyInput';

function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: 'ffdf', body: 'fdw' },
    { id: 2, title: 'awewe 2', body: 'Description' },
    { id: 3, title: 'cxvb 3', body: 'yjy' },

  ])


  const [selectedSort,setSelectedSort] = useState('')   
  const [searсhQuery,setSearchQuery] = useState('')       /* 3 делаем этот инпут управляемым, создаем состояние searсhQuery и функцию которая его будет изменять setSearchQuery, инициализируем useState */


  const sortedPosts = useMemo( () => {   /* 9 создаем хук useMemo, первым параметром передаем колбек, вторым массив зависимостей */
    console.log('ОТРАБОТАЛА ФУНКЦИЯ sortedPosts')
    if(selectedSort) {
      return [...posts].sort((a,b)=>a[selectedSort].localeCompare(b[selectedSort]))  /* 6 добавляем механизм сортировки */
    }
      return posts;
    }, [selectedSort, posts])

  const sortedAndSearcedPosts = useMemo(() =>{   /* 10 что бы поиск работал в отсортированном массиве используем опять useMemo, первым параметром колбек, вторым массив зависимостей */
    return sortedPosts.filter(post => post.title.toLowerCase().includes(searсhQuery))  /* 12 передаем фильтер колбер, потом в title вызываем функцию includes и передаем туда поисковую строку. Функция toLowerCase для игнорирования регистра  */
  },[searсhQuery,sortedPosts])

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
  }



  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))

  }

  const sortPosts = (sort) => {    
    setSelectedSort(sort);   
  }


  return (
    <div className="App">
      <PostForm create={createPost} />
      <hr style={{ margin: '15px 0' }}/> 

      {sortedAndSearcedPosts.length !== 0    /* 13 передаем условие "Посты не найдены" */
        ? <PostList remove={removePost} posts={sortedAndSearcedPosts} title="посты про Javascript" />    /* 11 передаем отсортированный массив постов */
        : <h1 style={{ textAlign: 'center' }}>
          Посты не найдены
        </h1>
      }

    </div>
  );
}

export default App;
