import React, { useState } from 'react';

function App() {

  const [count] = useState(0)  /* создаем функцию useState и импортируем ее */ 

  let likes = 10;     /* создаем переменную (счетчик) в нутри компонента  */

  function increment() {     /* создаем функцию с increment */
    likes += 1;
    console.log(likes);   /* вешаем слушатель события  */
  }

  function decrement() {     /* создаем функцию с переменной decrement */
    likes -= 1;
    console.log(likes);   /* вешаем слушатель события  */
  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <button onClick={increment}>Increment</button> { /* вешаем слушатель события onClick */ }
      <button onClick={decrement}>Decrement</button>

    </div>
  );
}

export default App;
