  const addNewPost = (e) => {     /* создаем эту функцию */
    e.preventDefault()          /* вызываем функцию preventDefault, которая предотвращает дефолтное поведение браузера */
    console.log(title)
    console.log(bodyInputRef.current.value)  /* передаем ссылку Ref, current - это дом элемент */
  }

  return (
    <div className="App">
      <form>
        {/* УПРАВЛЯЕМЫЙ КОМПОНЕНТ */}
        <MyInput

          value={title}    /* Связываем валью инпута с состонием title */
          onChange={e => setTitle(e.target.value)}    /* добавляем функцию onChange что бы видеть как пользователь что-то вводит */
