          type="text"
          placeholder='Описание поста' />
        <MyButton onClick={addNewPost}>Создать пост</MyButton>      {/* вешаем слушатель события на кнопку и при нажатии добавляем фугкцию */}
      </form>
      <PostList posts={posts} title="посты про js 1" />
    </div>
  );
}

export default App;
